import React, { useState } from "react";

import PhoneInputWithCountrySelect from "react-phone-number-input";
// import "react-phone-number-input/style.css";
import axios from "axios";
import { Box, Button, Grid, TextField } from "@material-ui/core";
import FileInput from "./file_input";
import { toast } from "react-toastify";
import { submitForm } from "./form_submission.service";

const JobSeeker = () => {
  const [phone, setPhone] = useState("");
  const [formData, setFormData] = useState({
    name: "",
    gender: "male",
    phone_number: "",
    age: 0,
    qualification: "",
    email: "",
    resume: null,
  });

  const [errors, setErrors] = useState({});
  console.log("ðŸš€ ~ JobSeeker ~ errors:", errors);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
    // validateField(name, value);
  };

  const handleFileChange = (e) => {
    const file = e.target.files ? e.target.files[0] : null;
    setFormData({ ...formData, resume: file });
    // validateField("resume", file);
  };

  const handlePhoneChange = (value) => {
    setPhone(value || "");
    setFormData({ ...formData, phone_number: value || "" });
    // validateField("phone_number", value || "");
  };

  // const validateField = (fieldName, value) => {
  //   const newErrors = { ...errors };

  //   switch (fieldName) {
  //     case "email":
  //       newErrors.email = value ? "" : "Email is required";
  //       break;
  //     case "phone_number":
  //       newErrors.phone_number = value ? "" : "Phone number is required";
  //       break;
  //     case "resume":
  //       newErrors.resume = value ? "" : "Resume file is required";
  //       if (value) {
  //         const allowedExtensions = ["pdf"];
  //         const fileExtension = value.name.split(".").pop().toLowerCase();
  //         if (!allowedExtensions.includes(fileExtension)) {
  //           newErrors.resume = "Only PDF files are allowed for resume";
  //         }
  //       }
  //       break;
  //     default:
  //       break;
  //   }

  //   setErrors(newErrors);
  // };

  const handleSubmit = async (e) => {
    e.preventDefault();

    Object.keys(formData).forEach((fieldName) => {
      validateField(fieldName, formData[fieldName]);
    });

    // Check if any errors exist
    const isFormValid = Object.values(errors).every((error) => !error);
    if (!isFormValid) {
      return;
    }

    try {
      await submitForm(formData);
      toast.success("Form submitted successfully");

      setFormData({
        name: "",
        gender: "male",
        phone_number: "",
        age: "",
        qualification: "",
        email: "",
        resume: null,
      });
      setPhone("");
    } catch (error) {
      toast.error(error.message || "Error submitting form");
      console.error("Error:", error);
    }
  };

  return (
    <form>
      <Box sx={{ p: 3 }}>
        <Grid container spacing={3}>
          <Grid item sm={12} lg={6} md={6} xs={12}>
            <TextField
              type="text"
              name="name"
              label="Name"
              margin="dense"
              fullWidth
              variant="outlined"
              placeholder="Name"
              value={formData.name}
              onChange={handleChange}
            />
          </Grid>

          <Grid item sm={12} lg={6} md={6} xs={12}>
            <PhoneInputWithCountrySelect
              international
              countryCallingCodeEditable={false}
              defaultCountry="IN"
              value={phone}
              onChange={handlePhoneChange}
              inputStyle={{
                height: "105px",
                border: "none", // Remove the default border
              }}
              style={{
                width: "100%", // Adjust width as needed
                padding: "12px 16px", // Match padding with TextField
                borderRadius: "4px", // Match border radius with TextField
                border: "1px solid rgba(0, 0, 0, 0.23)", // Match border with TextField
                "&:hover": {
                  borderColor: "rgba(0, 0, 0, 0.23)", // Match hover border color with TextField
                },
                "&.Mui-focused": {
                  borderColor: "rgba(0, 0, 0, 0.87)", // Match focused border color with TextField
                },
              }}
            />
            {/* {errors.phone_number && (
              <span style={{ color: "red", fontSize: "15px" }}>
                {errors.phone_number}
              </span>
            )} */}
          </Grid>

          <Grid item sm={12} lg={6} md={6} xs={12}>
            <TextField
              type="email"
              required
              label="Email"
              margin="dense"
              fullWidth
              variant="outlined"
              name="email"
              placeholder="Email"
              value={formData.email}
              onChange={handleChange}
            />
            {/* {errors.email && (
              <span style={{ color: "red", fontSize: "15px" }}>
                {errors.email}
              </span>
            )} */}
          </Grid>

          <Grid item sm={12} lg={6} md={6} xs={12}>
            {" "}
            <label className="block mb-2 text-sm font-medium">Gender</label>
            <div className="flex items-center">
              <input
                type="radio"
                id="male"
                name="gender"
                value="male"
                checked={formData.gender === "male"}
                onChange={handleChange}
                className="mr-1"
              />
              <label htmlFor="male" className="mr-4">
                Male
              </label>
              <input
                type="radio"
                id="female"
                name="gender"
                value="female"
                checked={formData.gender === "female"}
                onChange={handleChange}
                className="mr-1"
              />
              <label htmlFor="female" className="mr-4">
                Female
              </label>
              <input
                type="radio"
                id="other"
                name="gender"
                value="other"
                checked={formData.gender === "other"}
                onChange={handleChange}
                className="mr-1"
              />
              <label htmlFor="other">Other</label>
            </div>
          </Grid>

          <Grid item sm={12} lg={6} md={6} xs={12}>
            <TextField
              type="number"
              label="Age"
              margin="dense"
              fullWidth
              variant="outlined"
              name="age"
              placeholder="Age"
              value={formData.age}
              onChange={handleChange}
            />
          </Grid>
          <Grid item sm={12} lg={6} md={6} xs={12}>
            <TextField
              type="text"
              margin="dense"
              fullWidth
              variant="outlined"
              label="Qualification"
              name="qualification"
              placeholder="Qualification"
              value={formData.qualification}
              onChange={handleChange}
            />
          </Grid>
          <Grid item sm={12} lg={6} md={6} xs={12}>
            <FileInput id="resume" onChange={handleFileChange} />
            {/* {errors.resume && (
              <span style={{ color: "red", fontSize: "15px" }}>
                {errors.resume}
              </span>
            )} */}
          </Grid>
          <Grid item sm={12} lg={12} md={12} xs={12}>
            <Box
              sx={{
                display: "flex",
                justifyContent: "center",
                alignItems: "center",
              }}
            >
              <Button
                style={{ width: "10%", background: "blue", color: "white" }}
                type="submit"
                fullWidth
                variant="contained"
                onClick={handleSubmit}
              >
                Submit
              </Button>
            </Box>
          </Grid>
        </Grid>
      </Box>
    </form>
  );
};

export default JobSeeker;
