import React, { useState } from "react";

import PhoneInputWithCountrySelect from "react-phone-number-input";
// import "react-phone-number-input/style.css";
import axios from "axios";
import {
  Box,
  Button,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  Grid,
  TextField,
  Typography,
} from "@material-ui/core";
import FileInput from "./file_input";
import { toast } from "react-toastify";
import {
  hgherStudiesForm,
  sectionCSubmitForm,
  sendOtp,
  verifyOtp,
} from "./form_submission.service";

const SectionC = ({ onClose }) => {
  const [phone, setPhone] = useState("");
  const [otpOpen, setOtpOpen] = useState(false);
  const [otp, setOtp] = useState("");
  const [otpLogId, setOtpLogId] = useState("");
  const [formData, setFormData] = useState({
    contact_no: "",

    phone_number: "",

    email: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
    // validateField(name, value);
  };

  const handlePhoneChange = (value) => {
    setPhone(value || "");
    setFormData({ ...formData, phone_number: value || "" });
    // validateField("phone_number", value || "");
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      await sendOtp(formData.phone_number)
        .then((res) => {
          const data = res.data;
          toast.success(res?.message);
          setOtpLogId(data?.otpLogId);
          //   onClose();

          setOtpOpen(true);
        })
        .catch((err) => {
          console.log("err", err);
          toast.error(err?.response?.data?.error || "otp");
        });
    } catch (error) {
      toast.error("Error submitting form");
      console.error("Error:", error);
    }
  };

  const handleformSubmit = () => {
    try {
